import React, { Component } from "react";
import CountDisplay from "./CountDisplay";

class LatestCount extends Component {

      constructor(props) {
            super(props);
            this.state = { apiResponse: ""};

            this.callAPI = this.callAPI.bind(this);
            this.componentWillMount = this.componentWillMount.bind(this);
      }


      callAPI() {
            fetch("http://localhost:5000/currentCounts")
                  .then(res => res.text())
                  .then(res => this.setState({ apiResponse: res }));
            }

      componentWillMount() {
            this.callAPI();
                  }


      render() {
            return (
                  <>
                        <div className="redBanner"> Latest Counts </div>
                        <div id="latestCounts"> 
                              <CountDisplay Level={this.state.apiResponse}/>
                              <CountDisplay Level={this.state.apiResponse}/>
                              <CountDisplay Level={this.state.apiResponse}/>
                              <CountDisplay Level={this.state.apiResponse}/>
                              <CountDisplay Level={this.state.apiResponse}/>
                              

                        
                        </div>
                        <div className="redBanner"> Busiest Rooms</div>
                        <div id="busiestRooms"></div>
                  </>
            );
      }
}

export default LatestCount;
