import React, { Component } from "react";

class AdminTools extends Component {
    constructor(props) {
        super(props);
        this.updateGoogleSheet = this.updateGoogleSheet.bind(this);
    }

    updateGoogleSheet() {

        alert("Counts logged to your Google Spreadsheet!");

        var xhttp = new XMLHttpRequest();

        xhttp.onreadystatechange = function() {
                if (this.readyState == 4 && this.status == 200) {
                    console.log(xhttp.responseText);
                    console.log("Success");
                }
            };

        const url = "http://localhost:5000/updateGoogleSheet";

        xhttp.open("POST", url, true);
        xhttp.send("Update the Google Sheet");
    }

    render() {
          return (
                <>
                    <br></br>
                    <h5> Admin Tools </h5>
                    <button
                        type="button"
                        className="btn btn-primary sideBarButton"
                        onClick={this.updateGoogleSheet}
                    >
                            Log Weekly Spreadsheet
                    </button>

                    <div className="input-group mb-3 messageWidget">
                              <span
                                    className="input-group-text  counterFormInputBoxes"
                                    id="basic-addon1"
                              >
                                    {" "}
                              </span>
                              <input
                                    type="text"
                                    className="form-control counterFormInputBoxes"
                                    placeholder="Message"
                                    aria-label="Username"
                                    aria-describedby="basic-addon1"
                                    id = "time"
                              />
                              <button type="button" class="btn btn-success">Success</button>
                        </div>


                </>
          );
    }
}


export default AdminTools;
