import React, { Component } from "react";
import CountDisplay from "./CountDisplay";

class LatestCount extends Component {

      constructor(props) {
            super(props);
            this.state = {
                  floor3: 0,
                  floor2: 0,
                  floor1: 0,
                  floorA: 0,
                  floorB: 0
                  };

            this.getLatestCounts = this.getLatestCounts.bind(this);
            this.componentDidMount = this.componentDidMount.bind(this);
      }


      getLatestCounts() {
            fetch("http://localhost:5000/currentCounts")
                  .then(res => res.text())
                  .then(res => this.setState({apiResponse: res}));
            }

      componentDidMount() {
            this.getLatestCounts();
                  }


      render() {
            return (
                  <>
                        <div className="redBanner"> Latest Counts </div>
                        <div id="latestCounts"> 
                              <CountDisplay Level={this.state.apiResponse}/>
                              <CountDisplay Level={this.state.apiResponse}/>
                              <CountDisplay Level={this.state.apiResponse}/>
                              <CountDisplay Level={this.state.apiResponse}/>
                              <CountDisplay Level={this.state.apiResponse}/>
                              
                        </div>
                        <div className="redBanner"> Busiest Rooms</div>
                        <div id="busiestRooms">
                              <CountDisplay Level={this.state.apiResponse}/>
                              <CountDisplay Level={this.state.apiResponse}/>
                              <CountDisplay Level={this.state.apiResponse}/>
                              <CountDisplay Level={this.state.apiResponse}/>
                              <CountDisplay Level={this.state.apiResponse}/>
                        </div>
                  </>
            );
      }
}

export default LatestCount;
